mapper.update:
UPDATE master_order
       SET status = #{status}  , chief_id = #{chiefId}  WHERE
       order_id = #{orderId}

mapper.select:
SELECT mo.order_id as
       orderId, su.user_name as userName, mo.price as price, mo.create_time as createTime,
       mo.status as status from master_order mo left join sys_user su on mo.user_id = su.user_id 
       1=1  AND su.user_name LIKE CONCAT('%',
       #{userName}, '%') 
        AND mo.create_time >= #{beginTime} 
        AND mo.create_time <= #{endTime} 
        AND mo.status = #{status} 

       order by mo.create_time desc
